// src/components/AppointmentBooking.js
import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';
import { format, addDays } from 'date-fns';

const AppointmentBooking = () => {
  const { doctorId } = useParams();
  const history = useHistory();
  const [doctor, setDoctor] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState('');
  const [patientName, setPatientName] = useState('');
  const [appointmentType, setAppointmentType] = useState('');
  const [notes, setNotes] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/doctors/${doctorId}`)
      .then(response => setDoctor(response.data))
      .catch(error => console.error('Error fetching doctor:', error));
  }, [doctorId]);

  useEffect(() => {
    if (doctor) {
      axios.get(`http://localhost:5000/doctors/${doctorId}/slots?date=${format(selectedDate, 'yyyy-MM-dd')}`)
        .then(response => setAvailableSlots(response.data))
        .catch(error => console.error('Error fetching available slots:', error));
    }
  }, [doctorId, selectedDate, doctor]);

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setSelectedSlot('');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const appointmentData = {
      doctorId,
      date: `${format(selectedDate, 'yyyy-MM-dd')}T${selectedSlot}`,
      duration: 30,
      appointmentType,
      patientName,
      notes,
    };

    axios.post('http://localhost:5000/appointments', appointmentData)
      .then(response => {
        alert('Appointment booked successfully!');
        history.push('/appointments');
      })
      .catch(error => console.error('Error booking appointment:', error));
  };

  if (!doctor) return <div>Loading...</div>;

  return (
    <div>
      <h2>Book an Appointment with Dr. {doctor.name}</h2>
      <div>
        {[...Array(7)].map((_, index) => {
          const date = addDays(new Date(), index);
          return (
            <button key={index} onClick={() => handleDateChange(date)}>
              {format(date, 'MMM d')}
            </button>
          );
        })}
      </div>
      <div>
        {availableSlots.map(slot => (
          <button key={slot} onClick={() => setSelectedSlot(slot)}>
            {slot}
          </button>
        ))}
      </div>
      {selectedSlot && (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Patient Name"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Appointment Type"
            value={appointmentType}
            onChange={(e) => setAppointmentType(e.target.value)}
            required
          />
          <textarea
            placeholder="Notes"
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
          />
          <button type="submit">Book Appointment</button>
        </form>
      )}
    </div>
  );
};

export default AppointmentBooking;
